INSERT INTO `language_lines` VALUES (36, 'about_us', 'time-p-1', '{\"es\":\"Descubra Cuba con nuestros gu\\u00edas tur\\u00edsticos de habla inglesa favoritos, elegidos espec\\u00edficamente por su conocimiento y pasi\\u00f3n por su pa\\u00eds. Cada tour tambi\\u00e9n est\\u00e1 acompa\\u00f1ado por un l\\u00edder de viaje de Cuba Bien que se asegurar\\u00e1 de que su viaje exceda sus expectativas.\",\"en\":\"Discover Cuba with our favorite English-speaking Cuban tour guides, specifically chosen for their knowledge and passion for their country. Each tour is also accompanied by a Cuba Bien tour leader who will ensure that your trip exceeds your expectations.\"}', '2018-09-04 13:39:10', '2018-09-04 13:39:10');
