INSERT INTO `language_lines` VALUES (151, 'tour_introduccion', 'id9', '{\"es\":\"La Habana, capital de Cuba, es una maravillosa ciudad llena de historia y tradiciones. Durante este recorrido, los viajeros visitar\\u00e1n los mejores lugares de La Habana, as\\u00ed como la cordillera de Vi\\u00f1ales, una de las reservas naturales m\\u00e1s grandes de la isla. Como siempre, habr\\u00e1 un mont\\u00f3n de sabrosa comida cubana, as\\u00ed como visitas a lugares de valor hist\\u00f3rico y cultural.\",\"en\":\"Havana, Capital of Cuba, is a wonderful city full of history and traditions. During this tour, travelers will visit the best places in Havana, as well as the Vi\\u00f1ales mountain range, one of the largest natural reserves on the Island. As always, there will be plenty of tasty Cuban food, as well as visits to places of historical and cultural value.\"}', '2018-09-21 01:30:06', '2018-10-03 09:59:11');
