INSERT INTO `language_lines` VALUES (159, 'tour', 'header-p', '{\"es\":\"<p>Cuba es una isla encantadora llena de historia, cultura y personas c\\u00e1lidas y acogedoras.<\\/p><p>\\u00danete a nosotros mientras exploramos la magia detr\\u00e1s de este fascinante pa\\u00eds, junto con el esp\\u00edritu acogedor del pueblo cubano.<\\/p>\",\"en\":\"<p>Cuba is an enchanting island brimming with history, culture and warm welcoming people.<\\/p>\\n<p>Join us as we explore the magic behind this fascinating country, along with the welcoming spirit of the Cuban people.<\\/p>\"}', '2018-09-21 10:54:02', '2018-09-21 10:54:02');
